function CreditCardSvg({ size, companyCode, className, }) {
  const [ width, height, ] = [ 48, 35, ];
  const aspectRatio = width / height;
  let chosenCard;
  switch (companyCode) {
    case 1:
      chosenCard = masterCard;
      break;
    case 2:
      chosenCard = visa;
      break;
    case 4:
      chosenCard = amex;
      break;

    default:
      chosenCard = null;
      break;
  }
  return (
    <svg
      height={`${size}em`}
      width={`${size * aspectRatio}em`}
      viewBox={`0 0 ${width} ${height}`}
      className={className}
    >
      {chosenCard}
    </svg>
  );
}

CreditCardSvg.propTypes = propTypes;

CreditCardSvg.defaultProps = defaultProps;

export default CreditCardSvg;
